import React, { useState, useRef } from 'react';
import {
  Checkbox,
  FormControlLabel,
  Button,
  Typography,
} from '@material-ui/core';
import { CheckCircle, Error } from '@material-ui/icons';
import './App.css';

const generatePassword = (
  length,
  includeUppercase,
  includeLowercase,
  includeNumbers,
  includeSymbols
) => {
  const uppercaseChars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
  const lowercaseChars = 'abcdefghijklmnopqrstuvwxyz';
  const numberChars = '0123456789';
  const symbolChars = '!@#$%^&*()_-+=<>?/';

  let characters = '';
  if (includeUppercase) characters += uppercaseChars;
  if (includeLowercase) characters += lowercaseChars;
  if (includeNumbers) characters += numberChars;
  if (includeSymbols) characters += symbolChars;

  let password = '';
  for (let i = 0; i < length; i++) {
    const randomIndex = Math.floor(Math.random() * characters.length);
    password += characters.charAt(randomIndex);
  }

  return password;
};

const calculatePasswordStrength = (password) => {
  // Add your own password strength calculation logic here
  // This is a simple example, you may want to use external libraries for more accurate assessment
  return password.length >= 8 ? 'strong' : 'weak';
};

function App() {
  const [passwordLength, setPasswordLength] = useState(12);
  const [includeUppercase, setIncludeUppercase] = useState(true);
  const [includeLowercase, setIncludeLowercase] = useState(true);
  const [includeNumbers, setIncludeNumbers] = useState(true);
  const [includeSymbols, setIncludeSymbols] = useState(true);
  const [generatedPassword, setGeneratedPassword] = useState('');
  const [passwordStrength, setPasswordStrength] = useState(null);

  const inputLimitRef = useRef(null);

  const handleGeneratePassword = () => {
    const password = generatePassword(
      passwordLength,
      includeUppercase,
      includeLowercase,
      includeNumbers,
      includeSymbols
    );
    setGeneratedPassword(password);
    setPasswordStrength(calculatePasswordStrength(password));
  };

  const handleScroll = (e) => {
    e.preventDefault();
    const delta = Math.sign(e.deltaY);
    const newLength = Math.max(1, passwordLength + delta);
    setPasswordLength(newLength);
    inputLimitRef.current.textContent = newLength;
  };

  return (
    <div className="App">
      <Typography variant="h4" gutterBottom>
        Password Generator
      </Typography>
      <div
        className="scrollable-input-limit"
        ref={inputLimitRef}
        onWheel={handleScroll}
      >
        {passwordLength}
      </div>
      <FormControlLabel
        control={
          <Checkbox
            checked={includeUppercase}
            onChange={() => setIncludeUppercase(!includeUppercase)}
          />
        }
        label="Include Uppercase"
      />
      <FormControlLabel
        control={
          <Checkbox
            checked={includeLowercase}
            onChange={() => setIncludeLowercase(!includeLowercase)}
          />
        }
        label="Include Lowercase"
      />
      <FormControlLabel
        control={
          <Checkbox
            checked={includeNumbers}
            onChange={() => setIncludeNumbers(!includeNumbers)}
          />
        }
        label="Include Numbers"
      />
      <FormControlLabel
        control={
          <Checkbox
            checked={includeSymbols}
            onChange={() => setIncludeSymbols(!includeSymbols)}
          />
        }
        label="Include Symbols"
      />
      <Button variant="contained" color="primary" onClick={handleGeneratePassword}>
        Generate Password
      </Button>
      {generatedPassword && (
        <div>
          <Typography variant="h6" gutterBottom>
            Generated Password
          </Typography>
          <div>{generatedPassword}</div>
          <Typography variant="subtitle1" gutterBottom>
            Password Strength:{' '}
            {passwordStrength === 'strong' ? (
              <CheckCircle style={{ color: 'green' }} />
            ) : (
              <Error style={{ color: 'red' }} />
            )}{' '}
            {passwordStrength}
          </Typography>
        </div>
      )}
    </div>
  );
}

export default App;
