import React, { useState } from 'react';
import { TextField, Checkbox, FormControlLabel, Button, Typography } from '@material-ui/core';
import { CheckCircle, Error } from '@material-ui/icons';
import './App.css';

const generatePassword = (length, includeUppercase, includeLowercase, includeNumbers, includeSymbols) => {
  // ... (same as before)
};

const calculatePasswordStrength = (password) => {
  // ... (same as before)
};

function App() {
  const [passwordLength, setPasswordLength] = useState(12);
  const [includeUppercase, setIncludeUppercase] = useState(true);
  const [includeLowercase, setIncludeLowercase] = useState(true);
  const [includeNumbers, setIncludeNumbers] = useState(true);
  const [includeSymbols, setIncludeSymbols] = useState(true);
  const [generatedPassword, setGeneratedPassword] = useState('');
  const [passwordStrength, setPasswordStrength] = useState(null);

  const handleGeneratePassword = () => {
    const password = generatePassword(passwordLength, includeUppercase, includeLowercase, includeNumbers, includeSymbols);
    setGeneratedPassword(password);
    setPasswordStrength(calculatePasswordStrength(password));
  };

  return (
    <div className="App">
      <Typography variant="h4" gutterBottom>
        Password Generator
      </Typography>
      <TextField
        type="number"
        label="Password Length"
        value={passwordLength}
        onChange={(e) => setPasswordLength(e.target.value)}
        fullWidth
      />
      <FormControlLabel
        control={<Checkbox checked={includeUppercase} onChange={() => setIncludeUppercase(!includeUppercase)} />}
        label="Include Uppercase"
      />
      <FormControlLabel
        control={<Checkbox checked={includeLowercase} onChange={() => setIncludeLowercase(!includeLowercase)} />}
        label="Include Lowercase"
      />
      <FormControlLabel
        control={<Checkbox checked={includeNumbers} onChange={() => setIncludeNumbers(!includeNumbers)} />}
        label="Include Numbers"
      />
      <FormControlLabel
        control={<Checkbox checked={includeSymbols} onChange={() => setIncludeSymbols(!includeSymbols)} />}
        label="Include Symbols"
      />
      <TextField
        label="Scrollable Input Limit"
        type="number"
        value={passwordLength}
        onChange={(e) => setPasswordLength(e.target.value)}
        InputProps={{
          endAdornment: (
            <Typography variant="subtitle2" style={{ marginLeft: 10 }}>
              {passwordLength}
            </Typography>
          ),
        }}
        fullWidth
      />
      <Button variant="contained" color="primary" onClick={handleGeneratePassword}>
        Generate Password
      </Button>
      {generatedPassword && (
        <div>
          <Typography variant="h6" gutterBottom>
            Generated Password
          </Typography>
          <TextField value={generatedPassword} fullWidth readOnly />
          <Typography variant="subtitle1" gutterBottom>
            Password Strength:{' '}
            {passwordStrength === 'strong' ? (
              <CheckCircle style={{ color: 'green' }} />
            ) : (
              <Error style={{ color: 'red' }} />
            )}{' '}
            {passwordStrength}
          </Typography>
        </div>
      )}
    </div>
  );
}

export default App;
